import React, {useState} from 'react';

//React without JSX
const Star = () => React.createElement('div', null, 'This is a little star');

// function App() {
//   return (
//     <div className="App">
//       Start here.
//     </div>
//   );
// }

const App = () => {
    // return Star();
    const [isLampOn, setIsLampOn] = useState(false) //set off by default
    const lightSwitch = () => setIsLampOn(prev => !prev); //switch switches the boolean value to true once activated

    return (
        //the below tags work similar to classes in Java
        <Room>
            <Lamp lampOn={isLampOn} position='left'/>
            <LightSwitch
                name='one'
                callback={lightSwitch}
                switchOn={isLampOn}
            />
        </Room>
    )
}

export default App;
