    return (
        //the below tags are being used similar to classes in Java
        <Room>
            {/*the below attributes are called props, which control the properties of that tag*/}
            <Lamp lampOn={isLampOn} position='left'/>
            {/*<LightSwitch*/}
            {/*    name='one'*/}
            {/*    callback={lightSwitch}*/}
            {/*    switchOn={isLampOn}*/}
            {/*    position='left'*/}
            {/*/>*/}
        </Room>
    )
}

export default App;
