//importing module using es6 syntax
import {createGlobalStyle} from 'styled-components';

//exporting module for use
export const GlobalStyle = createGlobalStyle`
    //root variables to be used in our css selectors
    :root {
        --maxWidth: 1280px;
        --white: #fff;
        --lightGrey: #eee;
        --medGrey: #353535;
        --darkGrey: #1c1c1c;
        --fontSuperBig: 2.5rem;
        --fontBig: 1.5rem;
        --fontMed: 1.2rem;
        --fontSmall: 1rem;
    }

    //css
    * {
        box-sizing: border-box;
        font-family: 'Abel';
    }

    body {
        margin: 0;
        padding: 0;
    }

    h1 {
        font-size: 2rem;
        font-weight: 600;
        color: var(--white);
    }

    h3 {
        font-size: 1.1rem;
        font-weight: 600;
    }

    p {
        font-size: 1rem;
        font-weight: 600;
        color: var(--white);
    }
`;
